import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';

const URL = 'http://localhost:3002/'


function App() {
  const [task,setTask] = useState([]);
  const [tasks,setTasks] = useState('');

  useEffect(() => {
    axios.get(URL)
      .then((response) => {
        setTask(response.data)
      }).catch(error => {
        alert(error.response.data.error);
      })
  },[])

  function save() {
    const json = JSON.stringify({description: task});
    axios.post(URL + 'new',json,{
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((response) => {
        const addedObject = JSON.parse(json)

        addedObject.id = response.data.id

        setTask(task => [...task,addedObject])
        setTasks('')
      }).catch(error => {
        alert(error.response.data.error);
      })
  }

  function remove(id) {
    axios.delete(`${URL}delete/${id}`)
      .then(() => {
        const newListWithoutRemoved = task.filter((item) => item.id !==id)
        setTask(newListWithoutRemoved)
      }).catch(error => {
        alert(error.response.data.error);
      })

  }
  return(
    <div style = {{margin: '20px'}}>
      <h3>My tasks</h3>
      <form>
        <label>Add new</label>
        <input value={tasks} onChange={e => setTasks(e.target.value)}/>
        <button type='button' onClick={save}>Save</button>
      </form>
      <ol>
        {task.map(task => (
          <li key={task.id}>{task.description} <a href="#" onClick={() => remove(tasks.id)}>Delete</a></li>
        ))}
      </ol>
    </div>
  )
}

export default App;
